import { useState, useEffect } from "react";
import { Navbar, Nav, Container } from "react-bootstrap";
import logo from '../assets/img/logo.png';
import navIcon1 from "../assets/img/nav-icon1.svg";
import navIcon2 from '../assets/img/nav-icon2.png';
import navIcon3 from '../assets/img/nav-icon33.svg';
import { PopupModal } from "react-calendly";
import { HashScroll } from "react-hash-scroll";

import { Link } from "react-router-dom";

export const NavBar = () => {

  const [activeLink, setActiveLink] = useState('home');
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const onScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    }

    window.addEventListener("scroll", onScroll);

    return () => window.removeEventListener("scroll", onScroll);
  }, [])

  const onUpdateActiveLink = (value) => {
    setActiveLink(value);
  }

  const [isOpen, setIsOpen] = useState(false);
  const handleScroll = () => {
    // Add your logic to determine if the page is scrolled
    // For simplicity, let's assume that scrolling always sets scrolled to true
    setScrolled(true);
  };

  return (
    <Navbar expand='sm' className={scrolled ? 'scrolled' : ''} onScroll={handleScroll}>
      <Container>
        <Navbar.Brand>
          <a href="#home">
          <img src={logo} alt='Logo' className='logo' />
          </a>
        </Navbar.Brand>
        <Navbar.Toggle aria-controls='basic-navbar-nav'>
          <span className='navbar-toggler-icon'></span>
        </Navbar.Toggle>
        <Navbar.Collapse id='basic-navbar-nav'>
          <Nav className='ms-auto'>
            <Nav.Link
              className={activeLink === 'home' ? 'active navbar-link' : 'navbar-link'}
              onClick={() => onUpdateActiveLink('home')}>
                
              <HashScroll hash='#home'>
                Home
              </HashScroll>
            </Nav.Link>
            <Nav.Link
              className={activeLink === 'services' ? 'active navbar-link' : 'navbar-link'}
              onClick={() => onUpdateActiveLink('#services')}>
              <Link className='route' to='#skill'>
                Our Services
              </Link>
            </Nav.Link>
            <Nav.Link
              className={activeLink === 'specializations' ? 'active navbar-link' : 'navbar-link'}
              onClick={() => onUpdateActiveLink('specializations')}>
              <Link className='route' to='/specializations'>
                Testimonials
              </Link>
            </Nav.Link>
            <Nav.Link
              className={activeLink === 'specializations' ? 'active navbar-link' : 'navbar-link'}
              onClick={() => onUpdateActiveLink('specializations')}>
              <Link className='route' to='/specializations'>
                Contact Us
              </Link>
            </Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}
